    var dateForMySql = function(date){
        var aDate=[], m=1+date.getMonth(), d=date.getDate();
        aDate.push( date.getFullYear() );
        aDate.push( m<10?('0'+m):m );
        aDate.push( d<10?('0'+d):d );
        return aDate.join('-');
    };
