        var eChangeDpto = function(idDpto){
            if(typeof(idDpto)!=='undefined' && idDpto!=''){
                var req = new ClassAjax('post', 'index/resultmap');
                req.complete = fillProv;
                req.start('idDpto='+idDpto);
            }else console.log('problemas con:idDpto='+idDpto);
        };

